import React, { useState } from 'react';
import './App.css';

function App() {
  const [expandedTeacher, setExpandedTeacher] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const teachers = [
    {
      id: 1,
      name: 'Иванова Мария',
      subject: 'Математика',
      bio: 'Опыт преподавания 10 лет, автор учебных пособий. Специализация: подготовка к ЕГЭ и олимпиадам.',
      photo: '/images/teacher1.jpg'
    },
    {
      id: 2,
      name: 'Петров Алексей',
      subject: 'Физика',
      bio: 'Кандидат наук, участник международных конференций. Разработал собственную методику обучения.',
      photo: '/images/teacher2.jpg'
    },
    {
      id: 3,
      name: 'Сидорова Анна',
      subject: 'Программирование',
      bio: 'Бывший разработчик в Яндексе. Преподаёт Python и JavaScript для школьников.',
      photo: '/images/teacher3.jpg'
    }
  ];

  const courses = [
    {
      id: 1,
      title: 'Интенсив по ЕГЭ',
      description: 'Ускоренная подготовка к экзаменам'
    },
    {
      id: 2,
      title: 'Программирование',
      description: 'С нуля до первых проектов'
    }
  ];

  const toggleTeacher = (id) => {
    setExpandedTeacher(expandedTeacher === id ? null : id);
  };

  return (
    <div className="app">
      {/* Шапка с меню */}
      <header className="header">
        <div className="header-container">
          <div className="logo-container">
            <h1 className="logo">ИНТЕНСИВ</h1>
            <p className="logo-sub">Образовательный центр</p>
          </div>

          <button 
            className={`menu-button ${isMenuOpen ? 'open' : ''}`}
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            <span></span>
            <span></span>
            <span></span>
          </button>

          <nav className={`nav ${isMenuOpen ? 'open' : ''}`}>
            <a href="#about">О центре</a>
            <a href="#teachers">Преподаватели</a>
            <a href="#courses">Курсы</a>
            <a href="#contacts">Контакты</a>
          </nav>
        </div>
      </header>

      {/* Основное содержимое */}
      <main>
        {/* Блок о центре */}
        <section id="about" className="hero-section">
          <div className="hero-content">
            <h2>Максимальные результаты за минимальное время</h2>
            <p>Интенсивная подготовка с лучшими преподавателями</p>
            <button className="cta-button">Записаться</button>
          </div>
        </section>

        {/* Преподаватели */}
        <section id="teachers" className="teachers-section">
          <h2 className="section-title">Наши преподаватели</h2>
          <div className="teachers-grid">
            {teachers.map(teacher => (
              <div 
                key={teacher.id} 
                className={`teacher-card ${expandedTeacher === teacher.id ? 'expanded' : ''}`}
              >
                <div 
                  className="teacher-main"
                  onClick={() => toggleTeacher(teacher.id)}
                >
                  <img src={teacher.photo} alt={teacher.name} />
                  <div className="teacher-info">
                    <h3>{teacher.name}</h3>
                    <p className="subject">{teacher.subject}</p>
                  </div>
                  <div className={`arrow ${expandedTeacher === teacher.id ? 'up' : 'down'}`}>
                    ▼
                  </div>
                </div>

                {expandedTeacher === teacher.id && (
                  <div className="teacher-details">
                    <p>{teacher.bio}</p>
                    <button className="teacher-button">Записаться к преподавателю</button>
                  </div>
                )}
              </div>
            ))}
          </div>
        </section>

        {/* Курсы */}
        <section id="courses" className="courses-section">
          <h2 className="section-title">Наши программы</h2>
          <div className="courses-grid">
            {courses.map(course => (
              <div key={course.id} className="course-card">
                <h3>{course.title}</h3>
                <p>{course.description}</p>
                <button className="course-button">Подробнее</button>
              </div>
            ))}
          </div>
        </section>

        {/* Контакты */}
        <section id="contacts" className="contacts-section">
          <h2 className="section-title">Контакты</h2>
          <div className="contacts-content">
            <div className="contact-info">
              <p><strong>Адрес:</strong> г. Казань, ул. Братьев Батталовых, 20А</p>
              <p><strong>Телефон:</strong> +7 (123) 456-78-90</p>
              <p><strong>Email:</strong> info@intensiv.ru</p>
              <p><strong>Часы работы:</strong> Пн-Пт: 9:00-20:00</p>
            </div>
          </div>
        </section>
      </main>

      {/* Подвал */}
      <footer className="footer">
        <div className="footer-content">
          <p>© {new Date().getFullYear()} ИНТЕНСИВ</p>
          <div className="social-links">
            <a href="#">VK</a>
            <a href="#">Telegram</a>
            <a href="#">Instagram</a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
