import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css';

// Компонент главной страницы
const HomePage = () => (
  <main>
    <section className="hero-section">
      <div className="hero-content">
        <h1>Добро пожаловать в ИНТЕНСИВ</h1>
        <p>Образовательный центр для детей и подростков</p>
        <Link to="/register" className="cta-button">Записаться на курс</Link>
      </div>
    </section>
  </main>
);

// Компонент формы регистрации
const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    childName: '',
    childAge: '',
    course: ''
  });

  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Здесь будет отправка данных на сервер
    console.log('Отправка данных:', formData);
    setSuccess(true);
  };

  if (success) {
    return (
      <div className="success-message">
        <h2>Регистрация успешна!</h2>
        <p>Мы свяжемся с вами в ближайшее время.</p>
        <Link to="/" className="back-button">На главную</Link>
      </div>
    );
  }

  return (
    <div className="registration-container">
      <h2>Регистрация на курс</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Ваше имя:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Телефон:</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Имя ребенка:</label>
          <input
            type="text"
            name="childName"
            value={formData.childName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Возраст ребенка:</label>
          <input
            type="number"
            name="childAge"
            min="5"
            max="18"
            value={formData.childAge}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Выберите курс:</label>
          <select
            name="course"
            value={formData.course}
            onChange={handleChange}
            required
          >
            <option value="">-- Выберите курс --</option>
            <option value="math">Математика</option>
            <option value="physics">Физика</option>
            <option value="programming">Программирование</option>
          </select>
        </div>

        <button type="submit" className="submit-btn">
          Зарегистрироваться
        </button>
      </form>
    </div>
  );
};

// Главный компонент приложения
function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <Router>
      <div className="app">
        {/* Шапка сайта */}
        <header className="header">
          <div className="header-container">
            <div className="logo-container">
              <h1 className="logo">ИНТЕНСИВ</h1>
              <p className="logo-sub">Образовательный центр</p>
            </div>

            <button 
              className={`menu-button ${isMenuOpen ? 'open' : ''}`}
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              aria-label="Меню"
            >
              <span></span>
              <span></span>
              <span></span>
            </button>

            <nav className={`nav ${isMenuOpen ? 'open' : ''}`}>
